using FormFlowAPI.Configs;using FormFlowApi.Data.DbContexts;using FormFlowAPI.Settings;using Microsoft.EntityFrameworkCore;var builder = WebApplication.CreateBuilder(args);var config = builder.Configuration;var settings = new AppSettings(config);builder.Services.AddDbContext<ApplicationUsersContext>(options => options.UseNpgsql(settings.UserDbConnectionString));builder.Services.AddDbContext<FormDbContext>(options => options.UseNpgsql(settings.FormDbConnectionString));var authConfig = new AuthConfig(    appSettings: settings,    serviceCollection: builder.Services);builder.Services.AddControllers();builder.Services.AddEndpointsApiExplorer();builder.Services.AddSwaggerGen();builder.Services.AddScoped<AppSettings>();var app = builder.Build();if (app.Environment.IsDevelopment()){    app.UseSwagger();    app.UseSwaggerUI();}app.UseHttpsRedirection();app.UseAuthentication();app.UseAuthorization();app.MapControllers();app.Run();